import React, {Component} from 'react';
import Ingred_selection from './ingred_selection';
import Buying_choice from './buying_choice';
import Button from '@material-ui/core/Button';
import {Link} from 'react-router-dom';
/*
import Buying_choice from './buying_choice';
*/

class Home extends Component{
    state = {
        buy:{
            buy: false
        },
        ingredients:{
            campari: false,
            sweet_vermouth: false,
            gin: false,
            tonic: false,
            vodka: false,
            dry_vermouth: false,
            bourbon: false,
            bitters: false,
            rum: false,
            lime_juice: false,
            sugar: false,
            ginger_beer: false, 
            cognac: false,
            orange_liqueur: false,
            tequila: false,
            champagne: false,
    }
    }

    render(){
        return(
            <div>
                    <h1>Ingredient Selection</h1>              
                    


            <body>

        <FormControl component="fieldset" className={classes.formControl}>
        <FormLabel component="legend">Choose your Ingredients</FormLabel>
            <FormControlLabel
                control={<Checkbox checked={campari} onChange={handleChange('campari')} value="campari" />}
                label="Campari"
            />
        </FormControl>




                <Ingred_selection/>
                Do you want to buy the ingredients?
                <Buying_choice/>

                <Link to={{
                            pathname: '/matching_recipes/matching_recipes_full',
                            state: {
                                ingredients: this.state.ingredients,
                                buy: this.state.buy.buy
                            }
                        }}
                        ><Button variant="contained" color="primary">Submit </Button></Link>

            </body>
            </div>
        );
    }
}

export default Home;
